targets.expr:
max by(pool, name, type) (
  zfs_dataset_creation_timestamp{instance=~"$instance", pool=~"$pool", name="$dataset"}
  or
  zfs_dataset_creation{instance=~"$instance", pool=~"$pool", name="$dataset"}
  or
  zfs_dataset_creation_timestamp{instance=~"$instance", pool=~"$pool", name=~"$dataset/.*", invalid=~"$enable_children"}
  or
  zfs_dataset_creation{instance=~"$instance", pool=~"$pool", name=~"$dataset/.*", invalid=~"$enable_children"}
) * 1000

