targets.expr:
max by(pool, name, type) (
  zfs_dataset_used_bytes{instance=~"$instance", pool=~"$pool", name=~"$dataset"} /
  on (instance, pool) group_left()
  zfs_pool_size_bytes{instance="$instance", pool=~"$pool"}
)

max by(pool, name, type) (
  zfs_dataset_creation_timestamp{instance=~"$instance", pool=~"$pool", name=~"$dataset"}
  or
  zfs_dataset_creation{instance=~"$instance", pool=~"$pool", name=~"$dataset"}
) * 1000

max by(pool, name, type) (
zfs_dataset_snapshot_count_total{instance=~"$instance", pool=~"$pool", name=~"$dataset"}
)
